int dx[] = { 1,0,-1,0 };
int dy[] = { 0,1,0,-1 };

struct Grid {
	char c;
	bool black;
	bool white;
};

int main()
{
	while (true) {
		int w, h;
		cin >> w >> h;

		if (w == 0 && h == 0) {
			break;
		}

		vector<vector<Grid>> grid(h, vector<Grid>(w));

		for (int i = 0; i < h; i++) {
			string str;
			cin >> str;

			for (int j = 0; j < w; j++) {
				grid[i][j].c = str[j];
				grid[i][j].black = false;
				grid[i][j].white = false;
			}
		}

		for (int i = 0; i < h; i++) {
			for (int j = 0; j < w; j++) {
				if (grid[i][j].c == 'B') {
					queue<pair<int, int>> q;
					q.push({ i,j });

					while (q.empty() == false) {
						pair<int, int> temp = q.front();
						q.pop();

						for (int k = 0; k < 4; k++) {
							int ny = temp.first + dy[k];
							int nx = temp.second + dx[k];

							if (ny < h&&ny >= 0 && nx < w&&nx >= 0 && grid[ny][nx].c == '.'&&grid[ny][nx].black == false) {
								q.push({ ny,nx });
								grid[ny][nx].black = true;
							}
						}
					}
				}

				if (grid[i][j].c == 'W') {
					queue<pair<int, int>> q;
					q.push({ i,j });

					while (q.empty() == false) {
						pair<int, int> temp = q.front();
						q.pop();

						for (int k = 0; k < 4; k++) {
							int ny = temp.first + dy[k];
							int nx = temp.second + dx[k];

							if (ny < h&&ny >= 0 && nx < w&&nx >= 0 && grid[ny][nx].c == '.'&&grid[ny][nx].white == false) {
								q.push({ ny,nx });
								grid[ny][nx].white = true;
							}
						}
					}
				}
			}
		}

		int blackcount = 0;
		int whitecount = 0;

		for (int i = 0; i < h; i++) {
			for (int j = 0; j < w; j++) {
				if (grid[i][j].c == '.') {
					if (grid[i][j].black == true && grid[i][j].white == false) {
						blackcount++;
					}

					if (grid[i][j].white == true && grid[i][j].black == false) {
						whitecount++;
					}
				}
			}
		}

		cout << blackcount << " " << whitecount << endl;
	}

	return 0;
}

