    int ans[2] = {0};
    for(int k = 0 ; k < 2 ; k++ ){
      for(int i = 0 ; i < H ; i++ ){
	for(int j = 0 ; j < W ; j++ ){
	  if(grid[j][i] == '.'){
	    if((j+1 < W && grid[j+1][i] == c[k])||
	       (j-1 >= 0 && grid[j-1][i] == c[k])||
	       (i+1 < H && grid[j][i+1] == c[k])||
	       (i-1 >= 0 && grid[j][i-1] == c[k]))ans[k]+=bfs(j,i,k);//p();
	  }
	}
      }
    }
    cout << ans[0] <<" "<< ans[1] << endl;
  }
  return 0;
}

