void solve(int W,int h){
	vvi vv(h+2,vi(W+2));
	vvb w(h+2,vb(W+2,true)),b(h+2,vb(W+2,true));
	queue<pii> bl,wh;
	char ch;
	rep(i,h)rep(j,W){
		b[i+1][j+1]=0;
		w[i+1][j+1]=0;
		w[i+1][j+1]=false;
		b[i+1][j+1]=false;
		cin>>ch;
		if(ch=='W'){
			vv[i+1][j+1]=1;
			w[i+1][j+1]=true;
			wh.push(pii(i+1,j+1));
		}
		if(ch=='B'){
			vv[i+1][j+1]=-1;
			b[i+1][j+1]=true;
			bl.push(pii(i+1,j+1));
		}
	}
	while(!wh.empty()){
		int x,y;
		tie(x,y)=wh.front();wh.pop();
		rep(i,4){
			int tx=x+dx[i];
			int ty=y+dy[i];
			if(!w[tx][ty]&&vv[tx][ty]!=-1){
				wh.push(pii(tx,ty));
				w[tx][ty]=true;
			}
		}
	}
	while(!bl.empty()){
		int x,y;
		tie(x,y)=bl.front();bl.pop();
		rep(i,4){
			int tx=x+dx[i];
			int ty=y+dy[i];
			if(!b[tx][ty]&&vv[tx][ty]!=1){
				bl.push(pii(tx,ty));
				b[tx][ty]=true;
			}
		}
	}
	int answ=0,ansb=0;
	rep(i,h){
		rep(j,W){
			if(!w[i+1][j+1]&&b[i+1][j+1])ansb++;
			if(vv[i+1][j+1]==-1)ansb--;
			if(!b[i+1][j+1]&&w[i+1][j+1])answ++;
			if(vv[i+1][j+1]==1)answ--;
		}
	}
	cout<<ansb<<" "<<answ<<endl;
}

int main(){
	cin.tie(0);
	ios::sync_with_stdio(false);
	int h,w;
	while(cin>>w>>h,h)solve(w,h);
}

