string land[51];
int black[51][51],white[51][51],w,h,visited[51][51];
int dx[4] = {0, 0, 1, -1};
int dy[4] = {1, -1, 0, 0};

void bfs() {

    memset(visited, 0, sizeof(visited));
    memset(black, 0, sizeof(black));
    memset(white, 0, sizeof(white));

    queue<pair<int,int> > q;
    //black
    for(int i=0; i<h; ++i)
        for(int j=0; j<w; ++j)
            if(land[i][j] == 'B')
                q.push(make_pair(i,j));


    while(!q.empty()) {
        int y = q.front().first,x = q.front().second; q.pop();
        if(visited[y][x] != 0) continue;
        visited[y][x] = 1;
        for(int k=0; k<4; ++k) {
            int nx = x + dx[k],ny = y + dy[k];
            if(nx < 0 || nx > w-1 || ny < 0 || ny > h-1) continue;
            if(land[ny][nx] != '.') continue;
            black[ny][nx] = 1;
            q.push(make_pair(ny,nx));
        }
    }

    memset(visited, 0, sizeof(visited));
    for(int i=0; i<h; ++i)
        for(int j=0; j<w; ++j)
            if(land[i][j] == 'W')
                q.push(make_pair(i,j));


    while(!q.empty()) {
        int y = q.front().first,x = q.front().second; q.pop();
        if(visited[y][x] != 0) continue;
        visited[y][x] = 1;
        for(int k=0; k<4; ++k) {
            int nx = x + dx[k],ny = y + dy[k];
            if(nx < 0 || nx > w-1 || ny < 0 || ny > h-1) continue;
            if(land[ny][nx] != '.') continue;
            white[ny][nx] = 1;
            q.push(make_pair(ny,nx));
        }
    }

}

int main() {

    while(cin>>w>>h, w|h) {
        for(int i=0; i<h; ++i)
            cin>>land[i];
        bfs();
        int wh=0,bl=0;

        for(int i=0; i<h; ++i) {
            for(int j=0; j<w; ++j) {
                if(black[i][j] == 1 && white[i][j] == 0)
                    bl++;

                if(white[i][j] == 1 && black[i][j] == 0)
                    wh++;
            }
        }

        cout<<bl<<" "<<wh<<endl;
    }
}

