void bfs(char map[50][50]){
  queue <pii> q;
  int bc=0,wc=0;
  rep(i,h){
    rep(j,w){
      if(map[j][i]=='.'){
	q.push(pii(j,i));
	int count=0;
	pii state=pii(0,0);
	while(!q.empty()){
	  int cx=q.front().FST,cy=q.front().SEC;
	  q.pop();
	  if(map[cx][cy]=='.')
	    count++;
	  map[cx][cy]='#';
	  rep(r,4){
	    int nx=cx+dd[r],ny=cy+dd[r+1];
	    if(nx<0 || ny<0 || nx>=w || ny>=h || map[nx][ny]=='#')
