	      state.SEC=1;
	      continue;
	    }
	    q.push(pii(nx,ny));
	  }
	}
	if(state==pii(1,0))
	  bc+=count;
	if(state==pii(0,1))
	  wc+=count;
      } 
    }
  }
  printf("%d %d\n",bc,wc);
}

