void dfs(field &m, size_t h, size_t w, bool isBlack){
	for(size_t i = 0; i < 4; i++){
		int nexth = h+dx[i];
		int nextw = w+dy[i];
		auto &tmp = m[nexth][nextw];
		if(tmp == make_pair(-1,-1))continue;
		if(isBlack){
			if(tmp.first == 0 && tmp.second != 2){
				tmp = {1, tmp.second};
				dfs(m, nexth, nextw, true);
			}
		}
		else{
			if(tmp.second== 0 && tmp.first != 2){
				tmp = {tmp.first, 1};
				dfs(m, nexth, nextw, false);
			}
		}
	}
}

