typedef pair<int ,int> P;
char grid[50][50];
int W,H;
int dx[] = {0,0,1,-1},dy[] = {1,-1,0,0};
char c[2] = {'B','W'};
/*void p(){
  for(int i = 0 ; i < H ; i++ ){
