int bfs(int sx,int sy,int k){
  queue<P> que;
  int res[2] = {0};
  bool flag = true;
  que.push(P(sx,sy));
  while(!que.empty()){
    P p = que.front();
    que.pop(); 

    if(grid[p.first][p.second] == '.'){
      grid[p.first][p.second] = c[k];
      res[k]++;
    } 

    for(int i = 0 ; i < 4 ; i++ ){
      int nx = p.first + dx[i],ny = p.second + dy[i];
      if(0 <= nx && nx < W && 0 <= ny && ny < H && grid[nx][ny] == '.'){
	que.push(P(nx,ny));
	if((nx+1 < W && grid[nx+1][ny] == c[!k])||
	   (nx-1 >= 0 && grid[nx-1][ny] == c[!k])||
	   (ny+1 < H && grid[nx][ny+1] == c[!k])||
	   (ny-1 >= 0 && grid[nx][ny-1] == c[!k]))flag = false;
      }
    }
  }
  if(flag == true)return res[k];
  else return 0; 
}

int main(){
  while( cin >> W >> H , W+H ){

    for(int i = 0 ; i < H ; i++ ){
      for(int j = 0 ; j < W ; j++ ){
	cin >> grid[j][i];
      }
    }
    int ans[2] = {0};
    for(int k = 0 ; k < 2 ; k++ ){
      for(int i = 0 ; i < H ; i++ ){
	for(int j = 0 ; j < W ; j++ ){
	  if(grid[j][i] == '.'){
	    if((j+1 < W && grid[j+1][i] == c[k])||
	       (j-1 >= 0 && grid[j-1][i] == c[k])||
	       (i+1 < H && grid[j][i+1] == c[k])||
	       (i-1 >= 0 && grid[j][i-1] == c[k]))ans[k]+=bfs(j,i,k);//p();
	  }
	}
      }
    }
    cout << ans[0] <<" "<< ans[1] << endl;
  }
  return 0;
}

#include <iostream>
using namespace std;

char field[50][50];
bool used[50][50];
int height, width;
int b_count, w_count;
bool b, w;

int dx[] = {
	1, 0, -1, 0
};
int dy[] = {
	0, 1, 0, -1
};

int move(int i, int j){
	int count = 1;
	used[i][j] = true;
	for(int k = 0; k < 4; k++){
		if(i+dy[k] >= 0 && i+dy[k] < height &&
		   j+dx[k] >= 0 && j+dx[k] < width)
		{
			int i2 = i+dy[k], j2 = j+dx[k];
			if(field[i2][j2] == '.'){
				if(!used[i2][j2]) count += move(i2, j2);
			}else if(field[i2][j2] == 'B'){
				b = true;
			}else{
				w = true;
			}
		}
	}
	return count;
}


int main(){
	while(cin >> width >> height && (width || height)){
		for(int i = 0; i < height; i++){
			for(int j = 0; j < width; j++){
				cin >> field[i][j];
				used[i][j] = false;
			}
		}
		b_count = 0;
		w_count = 0;
		for(int i = 0; i < height; i++){
			for(int j = 0; j < width; j++){
				if(field[i][j] == '.' && !used[i][j]){
					b = false;
					w = false;
					int count = move(i, j);
					if(b && !w){
						b_count += count;
					}else if(w && !b){
						w_count += count;
					}
				}
			}
		}
		cout << b_count << " " << w_count << endl;
	}
	return 0;
}

#include <iostream>
#define REP(i, a, n) for(int i = (a); i < (n); i++)
using namespace std;

int W, H;
char A[80][80];
int dd[5] = { 0, 1, 0, -1, 0 };
bool v[80][80], s[2][80][80];

bool isin(int y, int x) { return 0 <= y && y < H && 0 <= x && x < W; }

void dfs(int y, int x, int c) {
  if(v[y][x] || A[y][x] != '.') return;
  v[y][x] = true;
  s[c][y][x] = true;
  REP(i, 0, 4) if(isin(y + dd[i], x + dd[i + 1])) dfs(y + dd[i], x + dd[i + 1], c);
}

int main(void) {
  while(cin >> W >> H, W || H) {
    REP(i, 0, H) cin >> A[i];

    REP(i, 0, 2) REP(j, 0, H) REP(k, 0, W) s[i][j][k] = false;
    REP(i, 0, H) REP(j, 0, W) if(A[i][j] != '.') {
      REP(k, 0, 4) if(isin(i + dd[k], j + dd[k + 1])) {
        REP(l, 0, H) REP(m, 0, W) v[l][m] = false;
        dfs(i + dd[k], j + dd[k + 1], A[i][j] == 'W');
      }
    }

    int ans[2] = { 0, 0 };
    REP(i, 0, 2) REP(j, 0, H) REP(k, 0, W) if(s[i][j][k] && !s[1 - i][j][k]) ans[i]++;
    cout << ans[0] << " " << ans[1] << endl;
  }
  return 0;
}

