const int mod=1000000007;
#define rep(i,n) for(int i = 0; i < n; i++)
#define FOR(i,a,b) for(int i = (a); i < (b); i++)
class Solve {
private:
public:
    bool is_last_;
    Solve()
    {

        int h,w;
        int dx[4]={0,1,0,-1};
        int dy[4]={1,0,-1,0};
        cin >> w>>h;
        if (h == 0)
        {
            is_last_ = true;
            return;
        }
        vector<vector<int>> b=vector<vector<int>> (h+2,vector<int>(w+2,mod));
        queue<array<int,3>> q;
        FOR(i,0,h){
            string s;
            cin>>s;
            FOR(j,0,w){
                if(s[j]=='.'){
                    b[i+1][j+1]=0;
                }else if(s[j]=='B'){
                    q.push({i+1,j+1,1});
                }else if(s[j]=='W'){
                    q.push({i+1,j+1,2});
                }   
            }
        }
        //cerr<<"test"<<endl;
        while(q.size()){
            auto tpl=q.front();
            q.pop();
            FOR(d,0,4){
                int ni=tpl[0]+dx[d];
                int nj=tpl[1]+dy[d];
                int c=tpl[2];
                if(b[ni][nj]>5)continue;
                if(b[ni][nj]&c)continue;
                b[ni][nj]|=c;
                q.push({ni,nj,c});
            }
        }
        vector<int> v(3,0);
        FOR(i,1,h+1){
            FOR(j,1,w+1){
                if(b[i][j]==1)v[1]++;
                if(b[i][j]==2)v[2]++;
            }
        }
        cout<<v[1]<<" "<<v[2]<<endl;
    }
};

int main()
{
    while (!Solve().is_last_);
}

#include <iostream>
#include <map>

using namespace std;

const int dx[] = {0, 1, 0, -1};
const int dy[] = {-1, 0, 1, 0};

int W, H;
string land[51];
pair<bool, bool> check[51][51]; // Black, White

void dfs(int x, int y, char ch)
{
    if (ch == 'B' && check[y][x].first)
        return;
    else if (ch == 'W' && check[y][x].second)
        return;

    if (land[y][x] == '.') {
        if (ch == 'B')
            check[y][x].first = true;
        else if (ch == 'W')
            check[y][x].second = true;
    }

    for (int i = 0; i < 4; i++) {
        int nx = x + dx[i];
        int ny = y + dy[i];

        if (nx < 0 || ny < 0)
            continue;
        if (nx >= W || ny >= H)
            continue;
        if (land[ny][nx] == '.')
            dfs(nx, ny, ch);
    }
}


int main()
{
    while (cin >> W >> H, W && H) {
        // Input
        for (int y = 0; y < H; y++) {
            cin >> land[y];
            for (int x = 0; x < W; x++) {
                check[y][x].first = false;
                check[y][x].second = false;                
            }
        }

        for (int y = 0; y < H; y++) {
            for (int x = 0; x < W; x++) {
                if (land[y][x] == 'B')
                    dfs(x, y, 'B');
                else if (land[y][x] == 'W')
                    dfs(x, y, 'W');
            }
        }

        int ab, aw;
        ab = aw = 0;
        for (int y = 0; y < H; y++)
            for (int x = 0; x < W; x++) {
                if (!check[y][x].first || !check[y][x].second) {
                    if (check[y][x].first)
                        ab++;
                    else if (check[y][x].second)
                        aw++;
                }
            }

        cout << ab << " " << aw << endl;
    }

    return 0;
}

