							d[t.x][t.y] = 1;
							if (B) ans1++;
							else ans2++;
							if (t.x > 0)
								q.push(H{ t.x - 1,t.y });
							if (t.x < h - 1)
								q.push(H{ t.x + 1,t.y });
							if (t.y > 0)
								q.push(H{ t.x,t.y - 1 });
							if (t.y < w - 1)
								q.push(H{ t.x,t.y + 1 });
						}
					}
				}
			}
		}
		cout << ans1 << " " << ans2 << endl;
	}
	getchar(); getchar();
}

