char bfs(int y, int x){
  if(field[y][x] != '.') return '.';
  queue<P> que;
  set<P> check;
  que.push(P(y, x));
  bool isW, isB;
  isW = isB = false;
  while(!que.empty()){
    if(isW && isB) return '.';
    P pos = que.front();
    que.pop();
    if(check.find(pos) != check.end()) continue;
    check.insert(pos);
    for(int i = 0 ; i < 4 ; i++){
      if(pos.first+dy[i] < 0 || pos.first+dy[i] >= h) continue;
      if(pos.second+dx[i] < 0 || pos.second+dx[i] >= w) continue;

      if(field[pos.first+dy[i]][pos.second+dx[i]] == 'W'){
	isW = true;
	continue;
      }
      else if(field[pos.first+dy[i]][pos.second+dx[i]] == 'B'){
	isB = true;
	continue;
      }
      else{
	que.push(P(pos.first+dy[i], pos.second+dx[i]));
      }
    }
  }

  if(isW && !isB) return 'W';
  else if(!isW && isB) return 'B';
  else return '.';
}

