ll w,h;

vector<vector<ll>> v;
vector<string> vs;

int dy[] = {0,1,0,-1};
int dx[] = {1,0,-1,0};

bool in(int y,int x){
    return y >= 0 && y < h && x >= 0 && x < w;
}

ll c(int y,int x){
    v[y][x] = -1;
    ll res = 0;
    rep(i,4){
        ll ny = y + dy[i];
        ll nx = x + dx[i];
        if(!in(ny,nx))continue;
        if(vs[ny][nx] == 'W'){
            res |= 1;
            continue;
        }
        else if(vs[ny][nx] == 'B'){
            res |= 2;
            continue;
        }
        else if(v[ny][nx] == -1)continue;
        ll num = c(ny,nx);
        res |= num;
    }
    return res;
}

void solve(int y,int x,int col){
    v[y][x] = col;
    rep(i,4){
        int ny = y + dy[i];
        int nx = x + dx[i];
        if(!in(ny,nx))continue;
        if(vs[ny][nx] == '.' && v[ny][nx] == -1)solve(ny,nx,col);
    }
}

void print(){
    rep(i,h){
        rep(j,w){
            printf("%2lld",v[i][j]);
        }
        cout << endl;
    }
}

int main(){
    while(cin >> w >> h && w){
        vs.clear();
        v.clear();
        vs.resize(h);
        v.resize(h);
        rep(i,h){
            v[i].resize(w,0);
            cin >> vs[i];
        }
        rep(i,h){
            rep(j,w){
                ll col;
                if(vs[i][j] == '.'){
                    col = c(i,j);
                    solve(i,j,col);
                // print();
                }
            }
        }
        ll ans[2] = {};
        rep(i,h){
            rep(j,w){
                // printf("%2lld",v[i][j]);
                if(v[i][j] == 2)ans[0]++;
                else if(v[i][j] == 1)ans[1]++;
            }
            // cout << endl;
        }
        cout << ans[0] << " " << ans[1] << endl;
    }
}

#include "iostream"
#include "vector"
#include "string"
#include "queue"
using namespace std;
int dx[4]={1,0,-1,0};
int dy[4]={0,1,0,-1};
bool black[50][50],white[50][50];
int main(void)
{
	int w,h;
	while (cin>>w>>h,w) {
		memset(black,0,sizeof(black));
		memset(white,0,sizeof(white));
		vector<string> in;
		for (int i = 0; i < h; i++) {
			string t; cin>>t;
			in.push_back(t);
		}

		for (int i = 0; i < h; i++) {
			for (int j = 0; j < w; j++) {
				int f=0;
				if(in[i][j]=='W') f=1;
				else if(in[i][j]=='B') f=2;

				if(in[i][j]!='.')
				{
					bool used[50][50];
					memset(used,0,sizeof(used));
					queue<int> xs,ys;
					xs.push(j); ys.push(i);
					while (!xs.empty()) {
						int x=xs.front(),y=ys.front();
						xs.pop(); ys.pop();
						used[y][x]=true;
						if(f==1) white[y][x]=true;
						else black[y][x]=true;
						for (int k = 0; k < 4; k++) {
							int nx=x+dx[k],ny=y+dy[k];
							if(0<=nx&&nx<w&&0<=ny&&ny<h&&!used[ny][nx]&&in[ny][nx]=='.')
							{
								xs.push(nx); ys.push(ny);
							}
						}
					}
				}
			}
		}

		int Black=0,White=0;
		for (int i = 0; i < h; i++) {
			for (int j = 0; j < w; j++) {
				if(in[i][j]!='B'&&black[i][j]&&!white[i][j]) Black++;
				else if(in[i][j]!='W'&&!black[i][j]&&white[i][j]) White++;
			}
		}
		cout<<Black<<" "<<White<<endl;
	}
	return 0;
}

