using namespace std;
using P = pair<char, int>;
int w, h;

vector<string> mp;
vector<vector<P>> memo;
int di[] = {-1, 0, 1, 0};
int dj[] = {0, 1, 0, -1};
P dfs(int i, int j) {

    if (mp[i][j] != '.') return make_pair(mp[i][j], 0);
    if (memo[i][j].second != -1) return memo[i][j];
    memo[i][j].second = 0;

    int cnt = 1;
    char last = '-';
    for (int k = 0; k < 4; ++k) {
        int ni = i + di[k];
        int nj = j + dj[k];
        if (ni < 0 || ni >= h || nj < 0 || nj >= w) continue;
        if (memo[ni][nj].second != -1) continue;
        char c;
        int ncnt;
        tie(c, ncnt) = dfs(ni, nj);
        if (c == '?') last = '?';
        else if (last == '-') last = c;
        else if (last != c && c != '-') last = '?';
        cnt += ncnt;
    }
    memo[i][j] = make_pair(last, cnt);
    //     DD(i, j);
    //     REP(i, h) {
    //     REP(j, w) {
    //         cout << memo[i][j].first;
    //     }
    //     cout << endl;
    // }
    return memo[i][j];
}
int main()
{
    cin.tie(0);
    ios::sync_with_stdio(false);
    while(1) {
        cin >> w >> h;
        if (w == 0) break;
        mp = vector<string>(h);
        memo = vector<vector<P>>(h, vector<P>(w, make_pair('.', -1)));
        REP(i, h) cin >> mp[i];
        int bcnt = 0;
        int wcnt = 0;
        REP(i, h) REP(j, w) {
            if (memo[i][j].second != -1 || mp[i][j] != '.') continue;
            int cnt;
            char c;
            tie(c, cnt) = dfs(i, j);
            // REP(i, h) {
            //     REP(j, w) {
            //         cout << memo[i][j].first;
            //     }
            //     cout << endl;
            // }
            // DD(c, cnt);
            if (c == 'B') bcnt += cnt;
            if (c == 'W') wcnt += cnt;
        }
        cout << bcnt << " " << wcnt << endl;
    }
}

