using namespace std;

const int INF = (1<<30) - 1;

int W, H;
int dh[] = {0, 0, 1, -1};
int dw[] = {1, -1, 0, 0};

inline bool in_field(int h, int w){
    return 0<=h && h<H && 0<=w && w<W;
}

void dfs1(vector<string> &field, int h, int w){
    for(int i=0; i<4; i++){
        int nh = h + dh[i];
        int nw = w + dw[i];
        if(in_field(nh, nw) && field[nh][nw] == '.'){
            field[nh][nw] = '1';
            dfs1(field, nh, nw);
        }
    }
}

void dfs2(vector<string> &field, int h, int w){
    for(int i=0; i<4; i++){
        int nh = h + dh[i];
        int nw = w + dw[i];
        if(in_field(nh, nw) && (field[nh][nw] == '.' || field[nh][nw] == '1')){
            field[nh][nw] = field[nh][nw] == '.' ? '2' : '3';
            dfs2(field, nh, nw);
        }
    }
}



int main(){
    //ios::sync_with_stdio(false);
    //cin.tie(0);
    while(cin>>W>>H, W|H){
        vector<string> field(H);
        for(int i=0; i<H; i++)
            cin >> field[i];

        for(int i=0; i<H; i++)
            for(int j=0; j<W; j++)
                if(field[i][j] == 'B')
                    dfs1(field, i, j);

        for(int i=0; i<H; i++)
            for(int j=0; j<W; j++)
                if(field[i][j] == 'W')
                    dfs2(field, i, j);

        int cnt1 = 0, cnt2 = 0;
        for(int i=0; i<H; i++)
            for(int j=0; j<W; j++)
                if(field[i][j] == '1')
                    cnt1++;
                else if(field[i][j] == '2')
                    cnt2++;

        cout << cnt1 << " " << cnt2 << endl;
    }

    return 0;
}

