#include <bits/stdc++.h>
#define INF INT_MAX/1000
using namespace std;

int w, h;
int g[50][50];
bool used[50][50][2];

const int dx[] = { 0, -1,  0,  1};
const int dy[] = {-1,  0,  1,  0};

bool infield(int x, int y){
    return 0 <= x && x < w && 0 <= y && y < h;
}

int dfs(int x, int y, int color){
    if(g[y][x] == color) return 0;
    if(g[y][x] != 0){
        return -INF;
    }
    used[y][x][color-1] = true;

    int res = 0;
    for(int d = 0; d < 4; d++){
        int nx = x + dx[d], ny = y + dy[d];
        if(!infield(nx, ny) || used[ny][nx][color-1]) continue;
        res += dfs(nx, ny, color);
    }

    return res+1;
}

int main(void){
    while(cin >> w >> h, w || h){
        bool pole_exist = false;
        for(int i = 0; i < h; i++){
            for(int j = 0; j < w; j++){
                char c; cin >> c;
                used[i][j][0] = used[i][j][1] = false;
                if(c == 'W' || c == 'B') pole_exist = true;
                if(c == 'W') g[i][j] = 1;
                else if(c == 'B') g[i][j] = 2;
                else g[i][j] = 0;
            }
        }

        if(pole_exist == false){
            cout << 0 << " " << 0 << endl;
            continue;
        }
        int white = 0, black = 0;
        for(int i = 0; i < h; i++){
            for(int j = 0; j < w; j++){
                if(used[i][j][0] == false)
                    white += max(dfs(j, i, 1), 0);
                if(used[i][j][1] == false)
                    black += max(dfs(j, i, 2), 0);
            }
        }

        cout << black << " " << white << endl;
    }

    return 0;
}

