          queue<zahyou> que;
          zahyou z;
          z.x = i;
          z.y = j;
          que.push(z);
          while(que.size()){
            printf("%d , %d",que.front().x,que.front().y);
            for(int k = 0;k < 4;k++){
              if(1 <= que.front().x && que.front().x <= h && 1 <= que.front().y && que.front().y <= w){
                if(!v[que.front().x + dx[k]][que.front().y + dy[k]].black && !v[que.front().x + dx[k]][que.front().y + dy[k]].wallB && !v[que.front().x + dx[k]][que.front().y + dy[k]].wallW){
                  z.x = que.front().x + dx[k];
                  z.y = que.front().y + dy[k];
                  que.push(z);
                  v[que.front().x + dx[k]][que.front().y + dy[k]].black = 1;
                }
              }
            }
            que.pop();
          }
        }
      }
