          countB++;
        }
        else if(v[i][j].white && !v[i][j].wallB && !v[i][j].wallW){
          countW++;
        }
      }
    }
    ans_B.push_back(countB);
    ans_W.push_back(countW);
  }
  for(int i = 0;i < ans_B.size();i++)cout << ans_B[i] << " " << ans_W[i] << endl;
}

