    rep(i,4){
        int nx=x+dx[i];
        int ny=y+dy[i];
        if(nx<0 or nx>=h or ny<0 or ny>=w) continue;
        if(used[nx][ny]) continue;
        ret |= dfs(nx,ny,used);
    }
    return ret;
