typedef pair<int,bool> BW;
int W,H;
string c[52];
bool f[52][52];

BW Solve(int h,int w){
    f[h][w]=true;
    queue<P> q;
    q.push(P(h,w));
    bool Bflag=false;
    bool Wflag=false;
    int ans=0;
    while(!q.empty()){
        P now=q.front();q.pop();
        ans++;
        int dh[4]={0,1,-1,0};
        int dw[4]={1,0,0,-1};
        for(int i=0;i<4;i++){
            int ch=now.first +dh[i];
            int cw=now.second+dw[i];
            if(0<=ch && ch<H && 0<=cw && cw<W && f[ch][cw]==false){

                if(c[ch][cw]=='B'){
                    Bflag=true;
                }else if(c[ch][cw]=='W'){
                    Wflag=true;
                }else{
                    q.push(P(ch,cw));
                    f[ch][cw]=true;
                }
            }
        }
    }
    if(Bflag && Wflag){
        return BW(0,false);
    }else if(Bflag){
        return BW(ans,false);
    }
    else if(Wflag){
        return BW(ans,true);
    }else{
        return BW(0,false);
    }
}

