//Surrounding Area
#include<bits/stdc++.h>
using namespace std;
#define TMP 9999
#define INF 1000

int n, m;
vector<vector<int> > board;

//4方向
int dir[4][2]={{-1,0}, {0,1}, {1,0}, {0,-1}};

void print(){
  for(int i=0; i<n; i++){
    for(int j=0; j<m; j++)
      printf("%5d", board[i][j]);
    puts("");
  }
}

bool solve(int x, int y, int c){
  if(!(0<=x && x<n && 0<=y && y<m))return true;//枠外
  if(board[x][y]==TMP)return true;//保留中
  if(board[x][y]==INF)return false;//誰の領土でもない
  if(board[x][y]==c)return true;
  if(board[x][y]==-c)return false;
  if(board[x][y]==2*c)return true;
  if(board[x][y]==-2*c)return false;
  board[x][y]=TMP;
  bool flag=true;
  for(int i=0; i<4; i++){
    if(!solve(x+dir[i][0], y+dir[i][1], c)){
      flag=false;
      break;
    }
  }
  if(flag){
    board[x][y]=c*2;
    return true;
  }
  else{
    board[x][y]=INF;
    return false;
  }
}

int main(){
  while(true){
    cin>>n>>m;
    board.clear();
    if(n==0 && m==0)break;
    for(int i=0; i<n; i++){
      vector<int> tmp;
      for(int j=0; j<m; j++){
        char c;
        cin>>c;
        int t;
        if(c=='.')t=0;
        if(c=='W')t=1;
        if(c=='B')t=-1;
        tmp.push_back(t);
      }
      board.push_back(tmp);
    }

    for(int i=0; i<n; i++){
      for(int j=0; j<m; j++){
        if(board[i][j]==1){
          for(int k=0; k<4; k++)solve(i+dir[k][0], j+dir[k][1], 1);
        }
        if(board[i][j]==-1){
          for(int k=0; k<4; k++)solve(i+dir[k][0], j+dir[k][1], -1);
        }
      }
    }
    /*
    puts("");
    print();
    puts("");
    */
    int nw=0, nb=0;
    for(int i=0; i<n; i++)
      for(int j=0; j<m; j++)
        if(board[i][j]==2)nw++;
        else if(board[i][j]==-2)nb++;
    printf("%d %d\n", nb, nw);
  }
}

#include <bits/stdc++.h>

using namespace std;

int w, h;
string field[50];
    int dx[4] = {1, 0, -1, 0};
    int dy[4] = {0, 1, 0, -1};

bool isIn(int x, int y){
     return 0 <= x && x < h && 0 <= y && y < w;
}

int check(int x, int y){
    bool used[50][50];
    for(int i=0; i < 50; i++) for(int j=0; j< 50; j++) used[i][j] = false;
    int result = 0;

    queue<pair<int, int> > q;
    q.push(make_pair(x, y));
    while(!q.empty()){
        pair<int, int> p = q.front(); q.pop();
        if(used[p.first][p.second]) continue;
        used[p.first][p.second] = true;
        for(int i=0; i< 4; i++){
            int nx = p.first+dx[i], ny = p.second+dy[i];
            if(!isIn(nx, ny)) continue;
            if(field[nx][ny] != '.'){
                if(result == 0){
                    if(field[nx][ny] == 'W') result = 1;
                    else result = 2;
                }
                else {
                    if(result == 2 && field[nx][ny] == 'W') return -1;
                    else if(result == 1 && field[nx][ny] == 'B') return -1;
                }
            } else {
                q.push(make_pair(nx, ny));
            }
        }
    }
    return result;
}

int main(){
    while(cin >> w >> h, w+h){
        for(int i=0; i< h; i++) cin >> field[i];

        int a = 0, b = 0;
        for(int i=0; i < h; i++){
            for(int j=0; j < w; j++){
                if(field[i][j] == '.'){
                    int r = check(i, j);
                    if(r == 1) a++;
                    else if(r == 2) b++;
                }
            }
        }
        cout << b << " " << a << endl;
    }
}

#include <iostream>
