using namespace std;

char land[55][55];
int white, black;
int count;
int w, h;
bool ww, bb;

void solve(int x, int y){
    if(land[x][y] == 'W'){
        ww = true;
        return;
    }else if(land[x][y] == 'B'){
        bb = true;
        return;
    }else if(land[x][y] == 'G'){
        return;
    }
    count++;
    land[x][y] = 'G';
    solve(x + 1, y);
    solve(x, y + 1);
    solve(x - 1, y);
    solve(x, y - 1);
}

int main(){
    while(cin >> w >> h, w||h){
        white = black = 0;
        rep(i, h + 2){
            rep(j,w + 2){
                land[i][j] = 'G';
