        range(i, 1, h+1){
            scanf("%s", land[i] + 1);
            land[i][w + 1] = 'G';
        }
        range(i, 1, h+1){
            range(j, 1, w+1){
                if(land[i][j] == '.'){
                    ww = bb = false;
                    count = 0;
                    solve(i,j);
                    if(ww && !bb)
                        white += count;
                    else if(!ww && bb)
                        black += count;
                }
            }
        }
        cout << black << ' ' << white << endl;
    }
}

