        }
        if(mp[ny][nx] == -3){
            white = true;
        }
        if(0 <= nx && nx < w && 0 <= ny && ny < h && mp[ny][nx] == -1){
            cnt += dfs(ny,nx,num,sum,white,black);
            cnt++;//自信の数をカウント
        }
    }
    return cnt;
}

int solve(){
    //B,W以外の領域に番号を振る
    int res = 0;
    for(int y = 0;y < h;y++){
        for(int x = 0;x < w;x++){
            if(mp[y][x] == -1){
                bool white = false,black = false;
                int sum = dfs(y,x,res,1,white,black)+1;//孤立領域の面積のカウント
                res++;
                if(black == true && white == false){
                    B += sum;
                }else if(black == false && white == true){
                    W += sum;
                }
            }
        }
    }
    return res;
}

