string func(int w,int h){
    vvector<char> board(h,vector<char>(w));
    vvector<int> color(h,vector<int>(w,0));
    rep(i,h){
        rep(j,w){
            board[i][j] = in<char>();
        }
    }
    method(tocolor,int,char x){
        if(x=='B')return 1;
        if(x=='W')return 2;
        return 0;
    };
    method(func,void,int y,int x,int c){
        if(min(y,x)<0 or y >= h or x >= w)return;
        if(tocolor(board[y][x]) and tocolor(board[y][x]) != c)return;
        if(color[y][x] bitand c)return;
        color[y][x] |= c;
        func(y+1,x,c);
        func(y-1,x,c);
        func(y,x+1,c);
        func(y,x-1,c);
    };

    rep(i,h){
        rep(j,w){
            if(tocolor(board[i][j])){
                func(i,j,tocolor(board[i][j]));
            }
        }
    }
    int black = 0;
    int white = 0;
    rep(i,h){
        rep(j,w){
            if(!tocolor(board[i][j])){
                if(color[i][j]==1){
                    ++black;
                }
                if(color[i][j]==2){
                    ++white;
                }
            }
        }
    }
    return to_string(black) + " " + to_string(white);
}

