using PII = pair<ll, ll>;
#define MP make_pair
int dx[4] = {-1, 0, 1,  0};
int dy[4] = { 0, 1, 0, -1};

string S[100];

PII pour(ll i, ll j){
  queue<PII> que;
  que.push(MP(i, j));
  S[i][j]='c';
  ll cnt = 1;
  bool touch_black = false;
  bool touch_white = false;
  while(!que.empty()){
    auto pa = que.front(); que.pop();
    rep(i, 4){
      ll ty = pa.first + dy[i];
      ll tx = pa.second+ dx[i];
      if(in_range(ty, tx)){
        if(S[ty][tx]=='.'){
          que.push(MP(ty, tx));
          S[ty][tx] = 'c';
          cnt++;
        }
        if(S[ty][tx]=='B') touch_black = true;
        if(S[ty][tx]=='W') touch_white = true;
      }
    }
  }
  // 0 : not touch
  // 1 : white touch
  // 2 : black touch
  // 3 : both touch
  ll status;
  if(touch_black && touch_white){
    status = 3;
  }else if(touch_black){
    status = 2;
  }else if(touch_white){
    status = 1;
  }else{
    status = 0;
  }
  return MP(status, cnt);
}

