      rep(i, H){
        cin >> S[i];
      }

      ll black = 0;
      ll white = 0;
      rep(i, H){
        rep(j, W){
          if(S[i][j]=='.'){
            auto pa = pour(i, j);
            if(pa.first==2){
              black += pa.second;
            }
            else if(pa.first==1){
              white += pa.second;
            }
          }
        }
      }
      p2(black, white);
    }

    return 0;
}

