#define loop(n, i) for(int i=0;i<n;i++)
#define all(v) v.begin(),v.end()
#define HERE cout << "HERE: " << __LINE__ << endl;
#define INSP(v) cout << v << " at " << __LINE__ << endl;

using namespace std;
typedef long long ll;

int f[50][50];

int dfs(vector<vector<int>> &m, int h, int w, vector<vector<char>> as, int t) {
    if (h < 0 || w < 0 || h >= m.size() || w >= m[h].size()) {
        return 0;
    }
    if (as[h][w] == 'B') return 1;
    if (as[h][w] == 'W') return 2;
    if (f[h][w]) return m[h][w];
    f[h][w] = 1;
    t |= m[h][w];
    t |= dfs(m, h-1, w, as, t);
    t |= dfs(m, h+1, w, as, t);
    t |= dfs(m, h, w+1, as, t);
    t |= dfs(m, h, w-1, as, t);
    m[h][w] = t;
    return t;
}

int main()
{
    int w, h;
    while (cin >> w >> h, w) {
        loop(50,i) loop(50,j) f[i][j] = 0;
        vector<vector<char>> as;
        as.resize(h);
        loop(h,i) {
            string s;
            cin >> s;
            for (char c:s) as[i].push_back(c);
        }

        vector<vector<int>> mat(h, vector<int>(w,0));
        loop(h,i){
            loop(w,j){
                if (as[i][j] == '.') {
                    dfs(mat, i, j, as, 0);
                }
            }
        }

        int ans_w = 0, ans_b = 0;
        for(auto mm:mat) {
            for (auto m:mm) {
                if (m == 1) ans_b++;
                if (m == 2) ans_w++;
            }
        }
        cout << ans_b << ' ' << ans_w << endl;

    }
}

