char a[52][52];
bool visit[52][52];
int u, cnt;

int dx[] = {-1, 0, 1, 0};
int dy[] = {0, -1, 0, 1};

void dfs(int y, int x){
	++cnt;

	for(int i = 0; i < 4; ++i){
		int ny = y + dy[i], nx = x + dx[i];
		switch( a[ny][nx] ){
			case '.':
				if( !visit[ny][nx] ){
					visit[ny][nx] = true;
					dfs(ny, nx);
				}
				break;
			case 'B':
				u |= 1;
				break;
			case 'W':
				u |= 2;
				break;
		}
	}
}

