			scanf(" %s", a[i] + 1);
		}

		for(int i = 1; i <= h; ++i)
		for(int j = 1; j <= w; ++j){
			if( !visit[i][j] && a[i][j] == '.' ){
				visit[i][j] = true;
				u = cnt = 0;
				dfs(i, j);
				ans[u] += cnt;
			}
		}

		printf("%d %d\n", ans[1], ans[2]);
	}
}

