#include <bits/stdc++.h>
using namespace std;
int main()
{
    for (int W, H;;) {
        cin >> W >> H;
        if (W == 0 and H == 0) { break; }
        vector<vector<int>> f(H, vector<int>(W, 0));
        for (int i = 0; i < H; i++) {
            for (int j = 0; j < W; j++) {
                char c;
                cin >> c, f[i][j] = c == '.' ? 0 : c == 'B' ? 1 : 2;
            }
        }
        int wans = 0, bans = 0;
        for (int i = 0; i < H; i++) {
            for (int j = 0; j < W; j++) {
                if (f[i][j] != 0) { continue; }
                vector<vector<bool>> used(H, vector<bool>(W, false));
                using P = pair<int, int>;
                constexpr int d[] = {-1, 0, 1, 0, -1};
                queue<P> q;
                used[i][j] = true, q.push({i, j});
                auto in = [&](const int y, const int x) { return 0 <= y and y < H and 0 <= x and x < W; };
                bool w = false, b = false;
                while (not q.empty()) {
                    const int y = q.front().first, x = q.front().second;
                    q.pop();
                    for (int i = 0; i < 4; i++) {
                        const int newy = y + d[i], newx = x + d[i + 1];
                        if (not in(newy, newx) or used[newy][newx]) { continue; }
                        if (f[newy][newx] == 1) {
                            b = true;
                            continue;
                        } else if (f[newy][newx] == 2) {
                            w = true;
                            continue;
                        } else {
                            used[newy][newx] = true, q.push({newy, newx});
                        }
                    }
                }
                if (w and not b) { wans++; }
                if (b and not w) { bans++; }
            }
        }
        cout << bans << " " << wans << endl;
    }
    return 0;
}

