bool visit[55][55];
char island[55][55];
const int dy[] = { -1, 0, 1, 0 };
const int dx[] = {  0, 1, 0,-1 };
int w, h;

bool dfs (int cx, int cy, char kui )
{
//	if (visit[cy][cx] ) return;

	visit[cy][cx] = true;

	rep (k, 4 ){
		int ny = cy + dy[k];
		int nx = cx + dx[k];
		if (ny < 0 || nx < 0 || ny >= h || nx >= w || visit[ny][nx] ) continue;
		if (island[ny][nx] == 'B' || island[ny][nx] == 'W' ){
			if ((kui == 'W' && island[ny][nx] == 'B' ) || (kui == 'B' && island[ny][nx] == 'W' ) ){
				return false;
			} // end if
			continue;
		} // end if
		if (dfs (nx, ny, kui ) ){
			island[ny][nx] = (kui == 'W' ? 'w' : 'b' );
		}else{
			island[ny][nx] = 'f';
		} // end if
 	} // end rep

	return true;
}

void dfs2 (int cx, int cy )
{
	island[cy][cx] = 'f';	
	visit[cy][cx] = true;
	rep (k, 4 ){
		int nx = cx + dx[k];
		int ny = cy + dy[k];
		if (nx < 0 || nx >= w || ny < 0 || ny >= h || visit[ny][nx] || island[ny][nx] == 'W' || island[ny][nx] == 'B' ) continue;
		dfs2 (nx, ny );
	} // end rep	
}

int main()
{
//	cut here before submit 
