	while (scanf ("%d %d", &w, &h ) && w && h ){
		memset (visit, false, sizeof (visit ) );
		rep (i, h ){
			rep (j, w ){
				scanf ( " %c", &island[i][j] );
			} // end rep
		} // end rep
		rep (y, h ){
			rep (x, w ){
				if (!visit[y][x] && (island[y][x] == 'W' || island[y][x] == 'B' ) ){
					dfs (x, y, island[y][x] );
				} // end if
			} // end rep
		} // end rep
		memset (visit, false, sizeof (visit ) );
		rep (y, h ){
			rep (x, w ){
				if (!visit[y][x] && island[y][x] == 'f' ){
					dfs2 (x, y );
				} // end if
			} // end rep
		} // end rep
		int W = 0, B = 0;
		rep (y, h ){
