#include <iostream>
#include <algorithm>
#define MAX 50
#define BLACK 'B'
#define WHITE 'W'
#define SPACE '.'
#define VISITED '#'

using namespace std;

int w, h;
int numB, numW;
int sum;
char data[MAX][MAX];
const int dx[4] = {1,0,-1,0};
const int dy[4] = {0,1,0,-1};

bool isOut(int x, int y) {
  if(x < 0 || x >= w) return true;
  if(y < 0 || y >= h) return true;
  return false;
}

void show() {
  for(int i = 0; i < h; i++) {
    for(int j = 0; j < w; j++) {
      cout << data[i][j];
    }
    cout << endl;
  }
  cout << endl;
}

void rec(int x, int y, char c) {
  data[y][x] = VISITED;
  if(sum != -1) sum++;
  for(int i = 0; i < 4; i++) {
    int nx = x + dx[i];
    int ny = y + dy[i];
    if(isOut(nx, ny)) continue;
    if(data[ny][nx] == VISITED) continue;
    if(data[ny][nx] == SPACE) {
      rec(nx, ny, c);
    } else if(data[ny][nx] != c) {
      sum = -1;
    }
  }
}

main() {
  while(1) {
    cin >> w >> h;
    if(w == 0 && h == 0) break;
    for(int i = 0; i < h; i++) {
      for(int j = 0; j < w; j++) {
	cin >> data[i][j];
      }
    }

    numB = numW = 0;
    for(int i = 0; i < h; i++) {
      for(int j = 0; j < w; j++) {
	if(data[i][j] != SPACE && data[i][j] != VISITED) {
	  for(int k = 0; k < 4; k++) {
	    int nx = j + dx[k];
	    int ny = i + dy[k];
	    if(isOut(nx, ny)) continue;
	    if(data[ny][nx] == VISITED) continue;
	    if(data[ny][nx] != SPACE) continue;
	    sum = 0;
	    rec(nx, ny, data[i][j]);
	    if(sum != -1) {
	      if(data[i][j] == BLACK) {
		numB += sum;
	      } else {
		numW += sum;
	      }
	    }
	  }
	}
      }
    }

    cout << numB << ' ' << numW << endl;
  }
}

#include<iostream>
using namespace std;
char t[100][100];
bool visited[100][100];
bool a,b;
int h,w,cnt;
void rec(int y,int x){
  if(t[y][x]=='B')a=true;
  if(t[y][x]=='W')b=true;
  if(t[y][x]!='.')return;
  if(visited[y][x])return;
  visited[y][x]=true;
  cnt++;
  if(y>0)rec(y-1,x);
  if(y+1<h)rec(y+1,x);
  if(x>0)rec(y,x-1);
  if(x+1<w)rec(y,x+1);
}
int main(){
  while(1){
    cin>>w>>h;
    if(h==0&&w==0)break;
    for(int i=0;i<h;i++)
      for(int j=0;j<w;j++)
	cin>>t[i][j],visited[i][j]=false;
    int suma=0,sumb=0;
    for(int i=0;i<h;i++){
      for(int j=0;j<w;j++){
	if(visited[i][j])continue;
	if(t[i][j]!='.')continue;
	a=b=cnt=false;
	rec(i,j);
	if(a&&!b)suma+=cnt;
	else if(!a&&b)sumb+=cnt;
      }
    }
    cout<<suma<<' '<<sumb<<endl;
  }
}

