int f[50][50];
bool contain(int y, int x){
    return (0 <= y && y < h && 0 <= x && x < w);
}

signed main(){
    while(1){
        cin >> w >> h;
        if(h + w == 0) break;
        vector<string> s(h);
        rep(i, 0, h) cin >> s[i];
        rep(i, 0, h) rep(j, 0, w) f[i][j] = 0;
        int bc = 0, wc = 0;
        rep(i, 0, h){
            rep(j, 0, w){
                if(s[i][j] == '.') continue;
                queue<P> q;
                q.push(P(i, j));
                int bt = (s[i][j] == 'W' ? 1 : 0);
                while(!q.empty()){
                    P p = q.front(); q.pop();
                    rep(k, 0, 4){
                        int ny = p.first + dy[k];
                        int nx = p.second + dx[k];
                        // cout << ny << " " << nx << endl;
                        if(!contain(ny, nx)) continue;
                        if(s[ny][nx] == '.'){
                            // if(s[ny][nx] == 'W' || s[ny][nx] == 'B') continue;
                            if(f[ny][nx] & (1 << bt)) continue;
                            f[ny][nx] |= (1 << bt);
                        // cout << p.first << " " << p.second << endl;

                            q.push(P(ny, nx));
                        }
                    }
                }
            }
        }
        rep(i, 0, h){
            rep(j, 0, w){
                // cout << f[i][j] << " ";
                if(f[i][j] == 1) bc++;
                if(f[i][j] == 2) wc++;
            }
            // cout << endl;
        }
        cout << bc << " " << wc << endl;
    }
}

#include<bits/stdc++.h>
using namespace std;
typedef pair<int,int> mypair;
int dx[]={1,-1,0,0};
int dy[]={0,0,1,-1};
void dfs(vector<vector<bool> > &map,set<mypair> &ans,mypair now){
	int x = now.first;
	int y = now.second;
	map[x][y] = false;
	for(int i=0;i<4;i++){
	int nx = x+dx[i];
	int ny = y+dy[i];
	if(map[nx][ny]){
		mypair next = mypair(nx,ny);
		ans.insert(next);
		dfs(map,ans,next);
	}
	}
	return ;
}

int main(){
	while(1){
	int w,h;
	cin>>w>>h;
	if(w==0)break;
	vector<vector<bool> > mapb(h+2,vector<bool>(w+2,false) ) ,mapw(h+2,vector<bool>(w+2,false) );
	vector<mypair> brack,white;
	for(int i=1;i<=h;i++)
		for(int j=1;j<=w;j++)
		{
			char a;
			cin>>a;
			if(a=='.'){
				mapb[i][j] = true;
				mapw[i][j] = true;
			}else if(a=='W')white.push_back(mypair(i,j) );
			else brack.push_back(mypair(i,j) );
		}
	set<mypair> ansb,answ;
	for(int i=0;i<brack.size();i++)dfs(mapb,ansb,brack[i]);
	for(int i=0;i<white.size();i++)dfs(mapw,answ,white[i]);
	int cntb=0,cntw=0;
	for(auto i:ansb){
		auto it = answ.find(i);
		if(it==answ.end())cntb++;
	}
	for(auto i:answ){
		auto it = ansb.find(i);
		if(it==ansb.end())cntw++;
	}
	cout<<cntb<<' '<<cntw<<endl;
	}

	return 0;
}

