    int w,h;
    while(true){
        cin>>w>>h;
        if(w==0)break;
        vector<vector<char>> m(h+2,vector<char>(w+2,'#'));
        queue<pair<int,int>> bq,wq;
        for (int i = 0; i < h; ++i) {
            string s;
            cin>>s;
            for (int j = 0; j < w; ++j) {
                m[i+1][j+1]=s[j];
                if(m[i+1][j+1]=='B')bq.push(make_pair(i+1,j+1));
                else if(m[i+1][j+1]=='W')wq.push(make_pair(i+1,j+1));
            }
        }
        vector<vector<int>> a(h+2,vector<int>(w+2,0));
        while(!bq.empty()){
            auto t=bq.front();
            bq.pop();
            for (int i = 0; i < 4; ++i) {
                int ny=t.first+vy[i];
                int nx=t.second+vx[i];
                if(a[ny][nx]!=1&&m[ny][nx]=='.'){
                    bq.push(make_pair(ny,nx));
                    a[ny][nx]+=1;
                }
            }
        }
        while(!wq.empty()){
            auto t=wq.front();
            wq.pop();
            for (int i = 0; i < 4; ++i) {
                int ny=t.first+vy[i];
                int nx=t.second+vx[i];
                if((a[ny][nx]&2)==0&&m[ny][nx]=='.'){
                    wq.push(make_pair(ny,nx));
                    a[ny][nx]+=2;
                }
            }
        }
        int ba=0,wa=0;
        for (int i = 1; i <= h; ++i) {
            for (int j = 1; j <= w; ++j) {
                if(a[i][j]==1)++ba;
                else if(a[i][j]==2)++wa;
            }
        }
        cout<<ba<<" "<<wa<<endl;
    }
    return 0;
}

