typedef pair<int,int> P;

int dxy[4] = {-1, 1, 0, 0};
int W, H;
char a[60][60];
int temp[60][60];

int huga(int x, int y, int z[60][60]){//cout<<y<<" "<<x<<endl;
	int w = 0, b = 0, s = 0, ans=-3, t;
	queue <P> Q;

	Q.push(P(y,x));

	while(!Q.empty()){
		P p = Q.front();Q.pop();
		int yy = p.first, xx = p.second;
		z[yy][xx] = 1;
		for(int i = 0; i <= 3; i++){
			int nx = xx+dxy[i], ny = yy+dxy[3-i];
			if(temp[ny][nx] == -1)return -1;
			if(a[ny][nx] == '.' && !z[ny][nx]) Q.push(P(ny,nx));
			else if(a[ny][nx] == 'B')b=1;
			else if(a[ny][nx] == 'W')w=1;
		}
	}

	if(w&&b || !w&&!b) ans = -1;
	else if(!w&&b)ans = 1;
	else if(!b&&w)ans =  0;
	else ans = -4;

	//cout<<ans<<endl<<endl;
	return ans;

}

