		if(!W&&!H)break;
		for(int i = 0; i <= W+1; i++){
			a[0][i] = a[H+1][i] = 0;
			temp[H+1][i] = temp[0][i] = 2;
		}


		for(int i = 0; i <= H+1; i++){
			a[i][0] = a[i][W+1] = 0;
			temp[i][0] = temp[0][i] = 2;
		}

		for(int i = 1; i <= H; i++){
