void dfsW(int y, int x){
  if (L[y][x]=='.')
    L[y][x]='P';
  for (int i=0;i<4;i++){
    int mx=x+dx[i];
    int my=y+dy[i];
    if ((0<=mx && mx<w) && (0<=my && my<h) && (L[my][mx]=='.'))
      dfsW(my,mx);
  }  
}

void dfsB(int y, int x){
  if (L[y][x]=='.')
    L[y][x]='Q';
  else if (L[y][x]=='P')
    L[y][x]='N';
  for (int i=0;i<4;i++){
    int mx=x+dx[i];
    int my=y+dy[i];
    if ((0<=mx && mx<w) && (0<=my && my<h) && (L[my][mx]=='.' || L[my][mx]=='P'))
      dfsB(my,mx);
  }  
}

