        fill(isW[0],isW[50],false);
        while(!B.empty()){
            int y,x;
            tie(y,x) = B.front(); B.pop_front();
            isB[y][x]=true;
            int d[5]={0,1,0,-1,0};
            for(int i=0;i<4;i++){
                int yy=y+d[i],xx=x+d[i+1];
                if(yy<0||yy>=h||xx<0||xx>=w) continue;
                if(!isB[yy][xx]&&A[yy][xx]=='.'){
                    B.emplace_back(yy,xx);
                }
            }
        }
        while(!W.empty()){
            int y,x;
            tie(y,x) = W.front(); W.pop_front();
            isW[y][x]=true;
            int d[5]={0,1,0,-1,0};
