int dx[] = {1,0,0,-1};
int dy[] = {0,1,-1,0};

signed main(){
	//IOS();
	vector<pint>ans;
	while(1){
		int h,w;
		cin>>h>>w;
		if(w+h==0)break;
		string s[w];
		rep(i,0,w)cin>>s[i];
		vector<vector<int>>used(w,vector<int>(h,0));
		int B=0,W=0;
		rep(i,0,w)rep(j,0,h){
			if(used[i][j]==0&&s[i][j]=='.'){
				stack<pair<int,int>>st;
				st.push({i,j});
				int black = 0,white = 0,cnt = 0;
				while(!st.empty()){
					int I = st.top().fi;
					int J = st.top().se;
					st.pop();
					if(used[I][J])continue;
					used[I][J] = true;
					cnt++;
					rep(k,0,4){
						int ni = I+dx[k];
						int nj = J+dy[k];
						if(ni<0||nj<0||w<=ni||h<=nj)continue;
						if(used[ni][nj])continue;
						if(s[ni][nj]=='B'){
							black = 1;
							continue;
						}
						if(s[ni][nj]=='W'){
							white = 1;
							continue;
						}
						st.push({ni,nj});
					}
				}
				if(black&&white)continue;
				if(black)B+=cnt;
				if(white)W+=cnt;
			}
		}
		ans.push_back({B,W});
	}
	for(auto e:ans){
		cout<<e.fi<<" "<<e.se<<endl;
	}
}

