int H,W;
vector<vector<bool>> used;
vector<string> G;
int cnt = 0;
bool ok = true;

bool solve(){
  cin >> W >> H;
  if(H==0 && W==0) return false;
  G = vector<string>(H);
  used = vector<vector<bool>>(H,vector<bool>(W,false));
  for(int i=0; i<H; i++){
    cin >> G[i];
  }
  function<void(int,int,char)> dfs = [&dfs](int h,int w,char c) -> int{
    used[h][w] = true;
    cnt++;
    for(int k=0; k<4; k++){
      int ny = h + dy[k];
      int nx = w + dx[k];
      if(ny<0 || ny>=H || nx<0 || nx>=W) continue;
      if(used[ny][nx]) continue;
      if(G[ny][nx] == c) continue;
      if(G[ny][nx]!=c && G[ny][nx]!='.'){
        ok = false;
        continue;
      }
      dfs(ny,nx,c);
    }
  };
  map<char,int> mp;
  for(int i=0; i<H; i++){
    for(int j=0; j<W; j++){
      if(G[i][j] != '.'){
        for(int k=0; k<4; k++){
          int ny = i + dy[k];
          int nx = j + dx[k];
          if(ny<0 || ny>=H || nx<0 || nx>=W) continue;
          if(used[ny][nx]) continue;
          if(G[ny][nx] != '.') continue;
          ok = true;
          cnt = 0;
          dfs(ny,nx,G[i][j]);
          if(ok){
            mp[G[i][j]] += cnt;
          }
        }
      }
    }
  }
