void print_number(int w, int h) {
  int sumb = 0, sumw = 0;
  for (int i = 0; i < h; i++) {
    for (int j = 0; j < w; j++) {
      if (judge[i][j] == BLACK) sumb++;
      else if (judge[i][j] == WHITE) sumw++;
    }
  }

  cout << sumb << ' ' << sumw << endl;
}

void dfs_b(int w, int h, int x, int y) {
  for (int i = 0; i < 4; i++) {
    int s = x + p[i], t = y + q[i];
    if (0 <= s && s <= w && 0 <= t && t <= h && a[t][s] == '.'
	&& !searchedb[t][s]) {
      judge[t][s] += BLACK;
      searchedb[t][s] = true;
      dfs_b(w, h, s, t);
    }
  }
}

void dfs_w(int w, int h, int x, int y) {
  for (int i = 0; i < 4; i++) {
    int s = x + p[i], t = y + q[i];
    if (0 <= s && s <= w && 0 <= t && t <= h && a[t][s] == '.'
	&& !searchedw[t][s]) {
      judge[t][s] += WHITE;
      searchedw[t][s] = true;
      dfs_w(w, h, s, t);
    }
  }
}

void count_black(int w, int h) {
  for (int i = 0; i < h; i++) {
    for (int j = 0; j < w; j++) {
      if (a[i][j] == 'B') {
	black[countb].first = j;
	black[countb].second = i;
	countb++;
      }
    }
  }
}

void count_white(int w, int h) {
  for (int i = 0; i < h; i++) {
    for (int j = 0; j < w; j++) {
      if (a[i][j] == 'W') {
	white[countw].first = j;
	white[countw].second = i;
	countw++;
      }
    }
  }
}

void clear_array() {
  for (int i = 0; i < MAX; i++) {
    for (int j = 0; j < MAX; j++) {
      judge[i][j] = 0;
      visitedw[i][j] = visitedb[i][j]  = false;
      searchedw[i][j] = searchedb[i][j] = false;
    }
  }
}

int main() {
