typedef pair<int,int> pii;

int w, h;
string s[50];
bool B[50][50] = {}, W[50][50] = {};

void solve_b(int y, int x, bool visited[50][50]){
  B[y][x] = true;
  REP(i,4){
    int ny = dy[i] + y, nx = dx[i] + x;
    if(ny >= 0 && ny < h && nx >= 0 && nx < w){
      if(!visited[ny][nx] && s[ny][nx] == '.'){
        visited[ny][nx] = true;
        solve_b(ny, nx, visited);
      }
    }
  }

}

void solve_w(int y, int x, bool visited[50][50]){
  W[y][x] = true;
  REP(i,4){
    int ny = dy[i] + y, nx = dx[i] + x;
    if(ny >= 0 && ny < h && nx >= 0 && nx < w){
      if(!visited[ny][nx] && s[ny][nx] == '.'){
        visited[ny][nx] = true;
        solve_w(ny, nx, visited);
      }
    }
  }

}



int main(){

