          visited[i][j] = true;
          solve_w(i, j, visited);
        }
      }
    }

    int ans_b = 0, ans_w = 0;



    REP(i,h){
      REP(j,w){
        if(s[i][j] == '.'){
          if(B[i][j] == true && !W[i][j]) ans_b++;
          else if(!B[i][j] && W[i][j]) ans_w++;
        }
      }
