using namespace std;

/* constant */

const int MAX_H = 50;
const int MAX_W = 50;

const int dxs[] = {1, 0, -1, 0};
const int dys[] = {0, 1, 0, -1};

/* typedef */

typedef pair<int,int> pii;

enum { NA = 0, EW = 1, EB = 2, EWB = 3, W = 7, B = 11};

/* global variables */

int h, w;
int flds[MAX_H][MAX_W];

/* subroutines */

/* main */

int main() {
  for (;;) {
    cin >> w >> h;
    if (w == 0) break;

    for (int y = 0; y < h; y++) {
      string line;
      cin >> line;
      for (int x = 0; x < w; x++) {
	switch (line[x]) {
	case '.': flds[y][x] = NA; break;
	case 'W': flds[y][x] = W; break;
	case 'B': flds[y][x] = B; break;
	}
      }
    }

    for (int y0 = 0; y0 < h; y0++)
      for (int x0 = 0; x0 < w; x0++) {
	int fld = flds[y0][x0];
	if (fld == W || fld == B) {
	  int cl = (fld == W) ? EW : EB;

	  queue<pii> q;
	  q.push(pii(x0, y0));

	  while (! q.empty()) {
	    pii u = q.front();
	    q.pop();

	    int ux = u.first;
	    int uy = u.second;

	    for (int di = 0; di < 4; di++) {
	      int vx = ux + dxs[di];
	      int vy = uy + dys[di];

	      if (vx >= 0 && vx < w && vy >= 0 && vy < h &&
		  ! (flds[vy][vx] & cl)) {
		flds[vy][vx] |= cl;
		q.push(pii(vx, vy));
	      }
	    }
	  }
	}
      }

    int nb = 0, nw = 0;

    for (int y = 0; y < h; y++)
      for (int x = 0; x < w; x++) {
	if (flds[y][x] == EB) nb++;
	if (flds[y][x] == EW) nw++;
      }

    printf("%d %d\n", nb, nw);
  }

  return 0;
}

