void spread(int y, int x, vector<vector<bool> >& resField, vector<pair<int, int> > enposes) {
  int dy[] = {-1, 0, 1, 0};
  int dx[] = {0, -1, 0, 1};
  int h = resField.size();
  int w = resField[0].size();
  if(y >= 0 && y < h && x >= 0 && x < w && !resField[y][x] && count(begin(enposes), end(enposes), make_pair(y, x)) == 0) {
    resField[y][x] = true;
    for(int i = 0; i < 4; ++i) spread(y + dy[i], x + dx[i], resField, enposes);
  }
}

#include <bits/stdc++.h>
using namespace std;

using ll = long long int;
using P = pair<ll, ll>;
using P3 = pair<ll,P>;
using PP = pair<P, P>;
constexpr ll INF = 1LL << 60;
constexpr ll MOD = ll(1e9) + 7;
constexpr int di[] = {0, 1, 0, -1};
constexpr int dj[] = {1, 0, -1, 0};
constexpr double EPS = 1e-8;

bool solve(){
    int w, h;
    cin >> w >> h;
    if(w == 0) return true;
    vector<string> s(h);
    for(int i=0;i<h;i++){
        cin >> s[i];
    }
    vector<vector<int> > c(h, vector<int>(w,0));
    vector<vector<bool> > visited(h, vector<bool>(w));
    for(int t=0;t<2;t++){
        for(auto& v : visited) fill(v.begin(), v.end(), false);
        queue<P> que;
        for(int i=0;i<h;i++){
            for(int j=0;j<w;j++){
                if(s[i][j] == (t%2 ? 'W' : 'B')) que.push(P(i,j));
            }
        }
        while(!que.empty()){
            auto p = que.front();
            que.pop();
            int i = p.first, j = p.second;
            for(int k=0;k<4;k++){
                int ni = i+di[k], nj = j+dj[k];
                if(ni < 0 || ni >= h) continue;
                if(nj < 0 || nj >= w) continue;
                if(visited[ni][nj] || s[ni][nj] != '.') continue;
                visited[ni][nj] = true;
                que.push(P(ni,nj));
                c[ni][nj] += (t%2 ? -1:1);
            }
        }
    }
    int cnt[2]{};
    for(int i=0;i<h;i++){
        for(int j=0;j<w;j++){
            if(c[i][j] > 0) cnt[0]++;
            if(c[i][j] < 0) cnt[1]++;
        }
    }
    cout << cnt[0] << " " << cnt[1] << endl;
    return false;
}

int main(){
    while(!solve());
    return 0;
}

