  else if(a[i][j]=='B'){b++;return 0;}
  int s=1;
  a[i][j]='#';
  s+=dfs(i+1,j);
  s+=dfs(i-1,j);
  s+=dfs(i,j+1);
  s+=dfs(i,j-1);
  return s;
}
