void serch_w(int x, int y){

	//if(?????????????????§??????&&??£??\???????????????'.'????????§??????)
	if (x + 1 < h && map[x + 1][y] == '.' && map[x + 1][y] != '2'){
		map[x + 1][y] = '2';
		serch_w(x + 1, y);
	}
	if (y + 1 < w && map[x][y + 1] == '.' && map[x][y + 1] != '2'){
		map[x][y + 1] = '2';
		serch_w(x, y + 1);
	}
	if (x - 1 >= 0 && map[x - 1][y] == '.' && map[x - 1][y] != '2'){
		map[x - 1][y] = '2';
		serch_w(x - 1, y);
	}
	if (y - 1 >= 0 && map[x][y - 1] == '.' && map[x][y - 1] != '2'){
