char dfs(int y, int x){
	if (c[y][x] != '.')return c[y][x];
	if (d[y][x])return 0;
	f[y][x] = true;

	char ret = -1;
	rep(i, 4){
		int tx = x + dx[i], ty = y + dy[i];
		if (cheak(tx, ty, w, h) && !f[ty][tx]){
